'From Squeak3.6 of ''6 October 2003'' [latest update: #5429] on 7 February 2004 at 3:33:28 am'!!FilePackage methodsFor: 'initialize' stamp: 'ar 2/7/2004 03:32'!fixStamps	classes do:[:aClass| 		self fixStampsIn: aClass.		aClass hasMetaclass ifTrue:[self fixStampsIn: aClass metaClass]].! !!FilePackage methodsFor: 'initialize' stamp: 'ar 2/7/2004 03:33'!fixStampsIn: aClass	| thisClass rec meth |	aClass exists ifFalse:[^self].	thisClass := aClass realClass.	aClass selectors do:[:sel|		rec := aClass sourceCode at: sel ifAbsent:[nil].		(rec isKindOf: ChangeRecord) ifTrue:[		(rec type == #method and:[rec stamp = '']) ifTrue:[			meth := thisClass compiledMethodAt: sel ifAbsent:[nil].			meth ifNotNil:[rec stamp: meth timeStamp].		].		].	].! !!PseudoClass methodsFor: 'fileIn/fileOut' stamp: 'ar 2/7/2004 01:04'!fileOutMethods: aCollection on: aStream	"FileOut all methods with selectors taken from aCollection"	| cat categories |	categories := Dictionary new.	aCollection do:[:sel|		cat := self organization categoryOfElement: sel.		cat = self removedCategoryName ifFalse:[			(categories includesKey: cat) 				ifFalse:[categories at: cat put: Set new].			(categories at: cat) add: sel].	].	categories associationsDo:[:assoc|		cat := assoc key.		assoc value do:[:sel|			aStream cr.			(self sourceCode at: sel) fileOutOn: aStream.		].	].! !